from pwn import *
from math import isqrt
from Crypto.Util.number import inverse

def solve(C, N, L):
  poss = []
  mlim = 2**20
  for x in range(1, mlim):
      a = x
      b = L-N-x**2
      c = x*N
      d = b**2-4*a*c
      if (d < 0):
          continue
      qform = (-b + isqrt(d))//(2*a)
      p = N//qform
      if ((p-x)*(qform-x) == L):
          poss.append((qform))
          break
  p, q = poss[0], N//poss[0]
  phi = (p-1)*(q-1)
  e = 65537
  d = inverse(e, phi)
  decrypted = pow(C, d, N)
  return decrypted

